package gov.utcourts.notifications.dataaccess;import java.sql.Connection;import java.sql.PreparedStatement;import java.sql.ResultSet;/** * This classes handles getting a connection to the data base. to change between straight JDBC and JNDI change the class name */public class DatabaseConnection {		private DatabaseConnection() {		super();	}			/**	 * Get a connection to the commondb database	 * @return Connection	 * @throws Exception	 */	public static Connection getJNDIConnectCommonDB() throws Exception {		return JNDIConnectCommonDB.getConnection();	}		/**	 * Get a connection to the commondb database	 * @return Connection	 * @throws Exception	 */	public static Connection getJDBCConnectCommonDB() throws Exception {		return JDBCConnect.getJDBCConnectCommonDB();	}		public static void main(String[] args) {		Connection conn = null;		try {			conn = DatabaseConnection.getJNDIConnectCommonDB();			PreparedStatement pstmt = conn.prepareStatement("SELECT * FROM user_info");			ResultSet rs = pstmt.executeQuery();			while (rs.next()) {				System.out.println(rs.getString(1));			}			rs.close();			pstmt.close();			System.out.println("Success.");		} catch (Exception e) {			System.err.println("Failed.");		} finally {			try {				conn.close();			} catch (Exception e) {				System.err.println("error closing connection");			}		}	}}